sp_widget.client_script:
function($rootScope,$scope,spUtil) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id, c.data.notes[idx]);
		$rootScope.noteID = id;
		c.noteId = id;
		$rootScope.$emit('selectNote', c.data.notes[idx]);
	}
	$rootScope.$on('updateNote', function(event,data) {
		console.log('updated event caught', data, c.data.notes);
		/*c.data.notes.forEach(function(n){
